import { useState,useHistory} from 'react'
import {toast} from 'react-toastify'


const Addcontact = () =>{

    // const history = useHistory();
    const [inputs , setInputs] = useState({
        name:"",
        email:"",
        contact:"",
    });
    const { name ,email , contact } =inputs;


    const onChange = (e) =>{
       setInputs({
           ...inputs,
           [e.target.name]: e.target.value
       });
    } 
    const onSubmitForm = async (e) =>{
        e.preventDefault();

        try {
            const body = { name , email,contact };
            if( !name ||!email ||!contact){
                return toast.error("Not all field are filled")
            }
            const response = await fetch("http://localhost:5000/contact" ,{            
                method:"POST",
                headers:{"content-type":"application/json"},
                body:JSON.stringify(body)
            });

            console.log(response);
            // history.push("/")


        } catch (err) {
            console.error(err.message)
        }
    }



   return(     

        <form  onSubmit={onSubmitForm} style={{ margin:"90px 0px 0px 300px"}}>
         <h1 className="text-start mt-3">Add Contact</h1>
         <div>

            <input type="text" name="name"
            placeholder="name" className='form-control my-3'
            style={{borderRadius:"10px 20px 30px 40px", width:"650px"}}
            value={name}
            // onChange={e=> setInputs(e.target.value)}
            onChange = { e=> onChange(e)}
            />

            <input type="email" name="email"
            placeholder="email" className='form-control  my-3'
            style={{borderRadius:"10px 20px 30px 40px", width:"650px"}}
            value={email}
            onChange = {e=> onChange(e)}
            />

             <input type="text" name="contact"
            placeholder="contact" className='form-control  my-3'
            style={{borderRadius:"10px 20px 30px 40px", width:"650px"}}
            value={contact}
            onChange = {e => onChange(e)}
            />
            <button type="submit" value="send" className="form-control btn btn-primary"
            style={{ marginTop: "20px", width:"650px",borderRadius:"10px 20px 30px 40px",fontSize:"20px",}}>
                Add</button>


            </div>


            </form>



   );
}
export default Addcontact;
