import React from "react";
import { Fragment } from "react";
import {Link } from 'react-router-dom'
// import AddIcon from '@mui/icons-material/Add';
// import { Add } from '@material-ui/icons';
import {useEffect, useState} from 'react'





const Listcontact = () =>{

  const [contacts ,setContacts] = useState({
    name:"",
    contact:""
  });

  const getContacts = async() =>{
    try {
      //fetch data from the database
      const response = await fetch("http://localhost:5000/allContacts")
      const jsonData = await response.json();

      setContacts(jsonData);
    } catch (err) {
      console.error(err.message)

    }
  }

  useEffect(()=>{
    getContacts();
  },[]);

  console.log(contacts)

   return(
       <Fragment>
         <h1  style={{fontSize:"45px"}} className="text-center mt-5 my-3"><i>My Contact List</i></h1>
           <div className="d-flex mt-3">
         <input type="search" placeholder="search" className="form-control my-4" 
         style={{borderRadius:" 20px 40px",width:"650px",marginLeft:"120px"}} />

         <button type="button"  style={{marginLeft:"250px" ,borderRadius:"30px 0px",width:"200px"}} 
          className="btn btn-primary my-4" >
             <Link style={{color:"white",textDecoration:"none",fontWeight:"600",fontSize:"20px"}} to="/new" >
             {/* <Add/> */}
             Add contact
             </Link>
             </button>

          </div>

         <div class="container">

  <table class="table table-striped">
    <thead>
      <tr>
        <th>Name</th>
        <th>Contact</th>
        <th>Email</th>
        <th>Edit</th>
        <th>Delete</th>
      </tr>
    </thead>
    <tbody>

      {contacts.map(contacts =>{
        <tr>
        <td>{contacts.name}</td>
        <td>{contacts.contact}</td>
        <td>{contacts.email}</td>

        <td>edit</td>
        <td>Delete</td>
      </tr>
      })}
      {/* <tr>
        <td>John</td>
        <td>25478907649</td>
        <td>john@example.com</td>
        <td>edit</td>
        <td>Delete</td>
      </tr> */}
      {/* <tr>
        <td>Mary</td>
        <td>Moe</td>
        <td>mary@example.com</td>
      </tr> */}

    </tbody>
  </table>
  </div>



       </Fragment>
    //    <i class='far fa-edit' style='font-size:24px'></i>
    //  <i class='fas fa-trash-alt' style='font-size:24px'></i>
    //<i class="bi bi-pencil-square"></i>
    //<i class="bi bi-plus"></i>
    //<i class="material-icons" style="font-size:48px;color:red">delete_forever</i>
    //<button style="font-size:24px">Button <i class="material-icons">add</i></button>
    //<i class="material-icons" style="font-size:48px;color:red">add</i>

    //<i class="fa fa-search"></i>
   );
}
export default Listcontact;
