import { useState} from 'react'
import {toast} from 'react-toastify'


const Addcontact = () =>{


    const [inputs , setInputs] = useState({
        name:"",
        email:"",
        contact:"",
    });
    const {email ,name ,contact} =inputs;


    const onChange = (e) =>{
       setInputs({
           ...inputs,
           [e.target.name]: e.target.value
       });
    } 
    const onSubmitForm =async e =>{
        e.preventDefault();

        if(!email || !name || !contact){
            return toast.error("Not all field are filled")
        }else{
            return toast.success("Contact successfully added to your list");


        }
    }



   return(     

        <form  onSubmit={onSubmitForm} style={{ margin:"25px 60px 50px 75px"}}>
         <h1 style={{marginTop:"25px"}}>Add Contact</h1>
         <div>

            <input type="text" name="name"
            placeholder="name" className='form-control my-3'
            style={{borderRadius:"20px 40px"}}
            value={name}
            onChange={e=> onChange(e)}
            />

            <input type="email" name="email"
            placeholder="email" className='form-control  my-3'
            style={{borderRadius:"10px 20px 40px"}}
            value={email}
            onChange={e=> onChange(e)}
            />

             <input type="text" name="contact"
            placeholder="contact" className='form-control  my-3'
            style={{borderRadius:"20px 40px"}}
            value={contact}
            onChange={e => onChange(e)}
            />
            <input type="submit" value="send" className="form-control btn btn-primary"
            style={{ marginTop: "20px"}}/>


            </div>


            </form>



   );
}
export default Addcontact;
