import React from "react";
import { Fragment } from "react";
import {Link } from 'react-router-dom'
import {useEffect, useState} from 'react'
import avatar from '../images/avatar.png';
import background from '../images/background1.jpg';
import Editcontact from "./editcontact";
import Footer from "./Footer";


const Listcontact = (props) =>{

  const [contacts ,setContacts] = useState([ ]);

        //~delete a contact
  const deleteContact = async id =>{
    try {

      const deleteContact = await fetch(`http://localhost:5000/contact/${id}`,{
        method:"DELETE"
      });
      // console.log(deleteContact);
      setContacts(contacts.filter(contacts=> contacts.user_id !== id))

    } catch (err) {
      console.error(err.message);

    }
  }
        //~listcontact
  const getContacts = async() =>{
    try {
      //fetch data from the database
      const response = await fetch("http://localhost:5000/contact")
      const jsonData = await response.json();
      setContacts(jsonData);

    } catch (err) {
      console.error(err.message)

    }
  }

  useEffect(()=>{
    getContacts();

  },[]);


     //~search for a contact\

     const [search ,setSearch] = useState("");   
    //  const searchHandler =(search) =>{

    //    setSearch(search);
    //    if(search !==" "){
    //    const newContactList = contacts.filter((contact)=>{
    //    Object.values(contact).join(" ").toLowerCase().includes(search.toLowerCase);
    //   //  console.log(newContactList)
    //   })
    //   }

    //   }


   return(
       <Fragment>

          <div  style={{backgroundImage:`url(${background})` ,backgroundRepeat:"no-repeat",
          backgroundBlendMode:"darken",position:"reflective",backfaceVisibility:"revert",
          backgroundSize:"cover",backgroundPosition:"center",height:"100%" ,width:"100%"}}>

         <h1  style={{fontSize:"45px"}} className="text-center mt-2 my-3"><i>CONTACTS MANAGER APP</i></h1>
           <div className="d-flex mt-3">


         {/* <input type="search" placeholder="search" className="form-control my-4 " 
         style={{borderRadius:" 20px 40px",width:"650px",marginLeft:"120px"}} 
        //  value = {search}
         onChange={(e) => {setSearch(e.target.value); }} 
          //  search={searchHandler} 
           /> */}
          <nav >
          <form class="form-inline"  >
          <input class="form-control mr-sm-2" type="text" placeholder="Search..." 
          value={search}
          style={{borderRadius:" 20px 40px",width:"550px",marginLeft:"120px"}}

           onChange={(e) => {setSearch(e.target.value) }} 

           />

          <button class="btn btn-primary" type="submit" 
          style={{borderRadius:" 40px 40px",width:"100px"}}>Search</button>
          </form>
          </nav>

         <button type="button"  style={{marginLeft:"250px" ,borderRadius:"30px 0px",width:"200px"}} 
          className="btn btn-primary sm-2" >
             <Link style={{color:"white",textDecoration:"none",fontWeight:"600",fontSize:"17px"}} to="/new" >
             {/* <Add/> */}
             Add contact
             </Link>
             </button>

          </div>

         <div class="container">

  <table class="table table-striped " >
     {/* <thead>
      <tr>
      <th>Profile</th>
        <th>Contacts</th>
        <th>Edit</th>
        <th>Delete</th>
      </tr> 
    </thead> */}
    <tbody>

      {contacts.map(contacts => (


          <tr key={contacts.user_id}>
           <div style={{width:"100px"}}>
          <Link to={`/contact/${contacts.user_id}`}>
          <td><img style={{width:"60px",right:"30px"}}src={avatar}/></td>

          <td><div class="text-dark" style={{fontFamily:"cursive"}}>{contacts.user_name}</div>
          <div class="text-primary" style={{fontFamily:"Gloria Hallelujah"}}>{contacts.user_email}</div>
          </td>
        {/* <td>{contacts.user_contact}</td> */}
           </Link>
        <td >
          <Editcontact  contacts={contacts}/>
        </td>

        <td><button onClick={()=>deleteContact(contacts.user_id)} 
        className="btn-sm btn btn-danger">Delete</button></td>
       </div>
      </tr>

      ))}

          </tbody>
          </table>
         <div>
       <Footer/>
         </div>
       </div>

       </div>
       </Fragment>

   );
}
export default Listcontact;
